package operators;

public class Inverted_Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = -100;
		int b = 100;
		boolean c = true;
		System.out.println(!c); // ! symbol denotes not
		System.out.println(~a); // ~(-ve)value then the value will become +ve by decreasing 1 number
		System.out.println(~b); // ~(+ve)value then the value will become -ve by decreasing 1 number

	}

}
