package operators;

public class Logical_bitwise_Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Another name for this is and , or operator
		int a=10;
		int b=20;
		int c=30;
		System.out.println(a<b && c>b); //&& refers to and
		System.out.println(a>b || c>b);//|| refers to or

	}

}
