package operators;

public class Shifted_Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/* shifted operators 2 types
		 1- left shifted
		 2- right shifted
		 */
		System.out.println(10<<4); // left shifter 10/(2^4)
		System.out.println(10>>4); // right shifter 10/(2^4)
		System.out.println(10<<8);
		System.out.println(10>>6);

	}

}
